CREATE OR REPLACE FUNCTION tiss_obter_taxa_proc_regra (nr_seq_proc_p bigint, vl_medico_p bigint, ie_via_acesso_p text, tx_procedimento_p bigint) RETURNS bigint AS $body$
DECLARE


vl_taxa_w		double precision	:= null;
vl_medico_w		double precision;
vl_proc_via_w		double precision;


BEGIN

/*select 	sum(vl_medico)
into 	vl_medico_w
from 	proc_paciente_valor
where 	nr_seq_procedimento = nr_seq_proc_p
and 	ie_tipo_valor = 2;
*/
if (ie_via_acesso_p = 'D') then

	vl_taxa_w	:= ((tx_procedimento_p*100)/70);
	--vl_proc_via_w := (vl_medico_w*0.70);
	--vl_taxa_w := round(((100*vl_medico_p)/vl_proc_via_w),4);
else
	if 	((ie_via_acesso_p) = 'M') then

		vl_taxa_w	:= ((tx_procedimento_p*100)/50);
		--vl_proc_via_w 	:= (vl_medico_w*0.50);
		--vl_taxa_w 	:= round(((100*vl_medico_p)/vl_proc_via_w),4);
	else
		vl_taxa_w := tx_procedimento_p;
	end if;
end if;

return 	vl_taxa_w;

end;
$body$
