CREATE OR REPLACE FUNCTION tiss_obter_tipo_consulta (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_seq_classif_atend_w	bigint;
cd_convenio_w		integer;
nr_atend_original_w	bigint;
ie_tipo_consulta_tiss_w	bigint;
cd_estabelecimento_w	smallint;

 
c01 CURSOR FOR 
SELECT	coalesce(IE_TIPO_CONSULTA_TISS,0) 
from	tiss_regra_tipo_consulta 
where	cd_estabelecimento					= cd_estabelecimento_w 
and	coalesce(nr_seq_classif_atend, nr_seq_classif_atend_w)	= nr_seq_classif_atend_w 
and 	coalesce(cd_convenio, cd_convenio_w)				= cd_convenio_w 
and	((coalesce(ie_atend_retorno, 'T') = 'T') or 
	 ((coalesce(nr_atend_original_w::text, '') = '') and (coalesce(ie_atend_retorno, 'T') = 'N')) or 
	 ((nr_atend_original_w IS NOT NULL AND nr_atend_original_w::text <> '') and (coalesce(ie_atend_retorno, 'T') = 'R'))) 
order 	by coalesce(cd_convenio,0), 
	coalesce(nr_seq_classif_atend, 0);


BEGIN 
 
select	MAX(a.nr_seq_classificacao), 
	MAX(b.cd_convenio), 
	MAX(a.nr_atend_original), 
	max(a.cd_estabelecimento) 
into STRICT	nr_seq_classif_atend_w, 
	cd_convenio_w, 
	nr_atend_original_w, 
	cd_estabelecimento_w 
from	atend_categoria_convenio b, 
	atendimento_paciente a 
where	a.nr_atendimento	= b.nr_atendimento 
and	b.nr_seq_interno	= obter_Atecaco_atendimento(a.nr_atendimento) 
and	a.nr_atendimento	= nr_atendimento_p;
 
open c01;
loop 
fetch c01 into 
	ie_tipo_consulta_tiss_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;
 
return ie_tipo_consulta_tiss_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
