SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_tipo_guia_autor (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


cd_estabelecimento_w	bigint;
ie_tipo_autorizacao_w	varchar(20);
ie_tipo_guia_w		varchar(10);
count_w			bigint;
cd_convenio_w		bigint;
ie_tiss_tipo_guia_w	varchar(20);

c01 CURSOR FOR
SELECT	ie_tiss_tipo_guia
from	tiss_regra_guia_autor
where	cd_estabelecimento					= cd_estabelecimento_w
and	ie_situacao					= 'A'
and	coalesce(cd_convenio, coalesce(cd_convenio_w,0))			= coalesce(cd_convenio_w,0)
and	coalesce(ie_tipo_autorizacao, coalesce(ie_tipo_autorizacao_w,'X'))= coalesce(ie_tipo_autorizacao_w,'X')
and	coalesce(ie_tipo_guia, coalesce(ie_tipo_guia_w,'X'))		= coalesce(ie_tipo_guia_w,'X')
order by coalesce(ie_tipo_autorizacao,0),
	coalesce(ie_tipo_guia,null),
	coalesce(cd_convenio,0);


BEGIN


if (coalesce(nr_sequencia_p,0) > 0) then

	select	cd_estabelecimento,
		ie_tipo_autorizacao,
		ie_tipo_guia,
		cd_convenio
	into STRICT	cd_estabelecimento_w,
		ie_tipo_autorizacao_w,
		ie_tipo_guia_w,
		cd_convenio_w
	from	autorizacao_convenio
	where	nr_sequencia	= nr_sequencia_p;

	if (coalesce(cd_estabelecimento_w::text, '') = '') then
		cd_estabelecimento_w	:= Obter_dados_autor_convenio(nr_sequencia_p,'E');
	end if;

	select	count(*)
	into STRICT	count_w
	from	tiss_regra_guia_autor
	where	cd_estabelecimento	= cd_estabelecimento_w;

	if (count_w > 0) then

		open C01;
		loop
		fetch C01 into
			ie_tiss_tipo_guia_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
		end loop;
		close C01;

	end if;

	if (coalesce(ie_tiss_tipo_guia_w::text, '') = '') then --Se não encontrou regra, mantem o padrão conforme o tipo de autorização
		if (ie_tipo_autorizacao_w = '1') then --Internação
			ie_tiss_tipo_guia_w	:= '1'; --Guia de solicitação internação
		elsif (ie_tipo_autorizacao_w = '2') then -- Prorrogação de Internação
			ie_tiss_tipo_guia_w	:= '8'; --Guia de solicitação de prorrogação de internação
		elsif (ie_tipo_autorizacao_w = '3') then
			ie_tiss_tipo_guia_w	:= '2'; --Guia de solicitação SP/SADT
		end if;

	end if;

end if;

return ie_tiss_tipo_guia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
