CREATE OR REPLACE FUNCTION tiss_obter_tipo_quimio (cd_estabelecimento_p bigint, ie_modalidade_trat_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10);
ie_tipo_quimio_tiss_w	varchar(10);

c01 CURSOR FOR
SELECT	ie_tipo_quimio_tiss
from	tiss_modalidade_tratamento
where	cd_estabelecimento					= cd_estabelecimento_p
and	coalesce(ie_modalidade_trat,coalesce(ie_modalidade_trat_p,'X'))	= coalesce(ie_modalidade_trat_p,'X')
order by coalesce(ie_modalidade_trat,'0');


BEGIN

open C01;
loop
fetch C01 into
	ie_tipo_quimio_tiss_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w	:= ie_tipo_quimio_tiss_w;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
