SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_tipo_transacao (nr_sequencia_autor_p bigint, ie_interno_p text) RETURNS varchar AS $body$
DECLARE


ds_tipo_sem_atend_w	varchar(255);
ds_retorno_w		varchar(255) := '';
nr_atendimento_w	bigint;


BEGIN

ds_tipo_sem_atend_w := obter_param_usuario(3004, 182, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ds_tipo_sem_atend_w);

select	max(nr_atendimento)
into STRICT	nr_atendimento_w
from	autorizacao_convenio
where	nr_sequencia	= nr_sequencia_autor_p;

if (coalesce(nr_atendimento_w,0) = 0) and (ds_tipo_sem_atend_w IS NOT NULL AND ds_tipo_sem_atend_w::text <> '') then
	ds_retorno_w	:= ds_tipo_sem_atend_w;
end if;

if (ie_interno_p = '70') then
	ds_retorno_w	:= 'CANCELA_GUIA';
end if;
if (ie_interno_p = '5') then
	ds_retorno_w	:= 'SOLICITA_STATUS_AUTORIZACAO';
end if;

if (coalesce(ds_retorno_w::text, '') = '') then
	ds_retorno_w	:= 'SOLICITACAO_PROCEDIMENTOS';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
