SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_vl_prot_imp (nr_seq_tiss_lote_p bigint) RETURNS bigint AS $body$
DECLARE


cd_convenio_w		convenio.cd_convenio%type;
nr_seq_retorno_w	tiss_cabecalho.nr_seq_retorno%type;
nr_seq_lote_hist_w	tiss_cabecalho.nr_seq_lote_hist%type;
nr_lote_imp_w		tiss_dem_lote.nr_lote%type;
nr_seq_protocolo_w	protocolo_convenio.nr_seq_protocolo%type;
nr_lote_w		bigint;
vl_protocolo_w		double precision;


BEGIN

select	max(a.nr_seq_retorno),
	max(a.nr_seq_lote_hist),
	max(d.nr_lote)
into STRICT	nr_seq_retorno_w,
	nr_seq_lote_hist_w,
	nr_lote_imp_w
from	tiss_cabecalho a,
	tiss_demonstrativo b,
	tiss_dem_fatura c,
	tiss_dem_lote d
where	a.nr_sequencia	= b.nr_seq_cabecalho
and	b.nr_sequencia	= c.nr_seq_demonstrativo
and	c.nr_sequencia	= d.nr_seq_fatura
and	d.nr_sequencia	= nr_seq_tiss_lote_p;

if (nr_seq_retorno_w IS NOT NULL AND nr_seq_retorno_w::text <> '') then

	select	max(cd_convenio)
	into STRICT	cd_convenio_w
	from	convenio_retorno
	where	nr_sequencia	= nr_seq_retorno_w;
	
elsif (nr_seq_lote_hist_w IS NOT NULL AND nr_seq_lote_hist_w::text <> '') then

	select	max(cd_convenio)
	into STRICT	cd_convenio_w
	from	lote_auditoria
	where	nr_sequencia	= nr_seq_lote_hist_w;

end if;

vl_protocolo_w	:= null;

nr_lote_w	:= somente_numero(substr(nr_lote_imp_w,1,15));

if (cd_convenio_w IS NOT NULL AND cd_convenio_w::text <> '') and (coalesce(nr_lote_w,0) > 0) then
	
	
	nr_seq_protocolo_w := Tiss_Obter_Seq_Protocolo_Xml(cd_convenio_w, nr_lote_w);

	if (coalesce(nr_seq_protocolo_w::text, '') = '') then

		begin
		select	nr_seq_protocolo
		into STRICT	nr_seq_protocolo_w
		from	protocolo_convenio
		where	cd_convenio		= cd_convenio_w
		and	ie_status_protocolo	= 2
		and	nr_seq_protocolo	= nr_lote_w  LIMIT 1;
		exception
		when others then
			nr_seq_protocolo_w	:= null;
		end;
	
	end if;
	
	
	if (nr_seq_protocolo_w IS NOT NULL AND nr_seq_protocolo_w::text <> '') then		
		select	obter_total_protocolo(nr_seq_protocolo_w)
		into STRICT	vl_protocolo_w
		;
	end if;

end if;

return	vl_protocolo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION tiss_obter_vl_prot_imp (nr_seq_tiss_lote_p bigint) FROM PUBLIC;

