SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_somente_caract_permitido (ds_conteudo_p text, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retonar apenas os caracteres permitidos
-------------------------------------------------------------------------------------------------------------------

OPS - Controle de Contestações
Locais de chamada direta: 
[X]  Objetos do dicionário [ ]  Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atenção: IE_OPCAO_P

A	- Alfabético de A à Z, maiúsculas e minúsculas e brancos
N	- Numérico de 0 à 9
AN	- Alfabético de A à Z maiúsculas e minúsculas e Numérico de 0 à 9 e brancos 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/						
ds_retorno_w		varchar(4000);
ds_conteudo_w		varchar(4000);


BEGIN
ds_conteudo_w := trim(both ds_conteudo_p);

if (ie_opcao_p = 'A') then
	ds_conteudo_w := elimina_acentuacao(ds_conteudo_w);
	ds_conteudo_w := replace(replace(replace(replace(ds_conteudo_w,'Ñ','N'),'ñ','n'),'Æ','AE'),'æ','ae');
	ds_retorno_w := regexp_replace(ds_conteudo_w, '[^a-zA-Z ]', '');

elsif (ie_opcao_p = 'N') then
	ds_retorno_w := regexp_replace(ds_conteudo_w, '[^0-9]', '');

elsif (ie_opcao_p = 'AN') then
	ds_conteudo_w := elimina_acentuacao(ds_conteudo_w);
	ds_conteudo_w := replace(replace(ds_conteudo_w,'Ñ','N'),'ñ','n');
	ds_retorno_w := regexp_replace(ds_conteudo_w, '[^a-zA-Z0-9 ]', '');
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
