CREATE OR REPLACE FUNCTION tit_pagar_obter_status_leg (ie_situacao_p text, dt_vencimento_atual_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10);

/*
V	Vencido
AV	Ã€ vencer
C	Cancelado
D 	Desdobrado
L	Liquidado
T	Transferido
*/
BEGIN

if (ie_situacao_p = 'A') and (trunc(dt_vencimento_atual_p) >= trunc(clock_timestamp())) then
	ds_retorno_w 	:= 'AV';

elsif (ie_situacao_p = 'A') and (trunc(dt_vencimento_atual_p) < trunc(clock_timestamp())) then
	ds_retorno_w	:= 'V';

elsif (ie_situacao_p in ('C','D','L','T')) then
	ds_retorno_w 	:= ie_situacao_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
