CREATE OR REPLACE FUNCTION tnm_obter_qtd_fatores (fatores_p text) RETURNS bigint AS $body$
DECLARE

tamanho_w bigint;
item_w varchar(1);
retorno_w bigint := 0;
BEGIN

if ((fatores_p IS NOT NULL AND fatores_p::text <> '')
    and length(trim(both fatores_p)) > 0) then

    tamanho_w := length(fatores_p);

    if (tamanho_w > 0) then
      retorno_w := 1;
    end if;

   for i in 1..tamanho_w loop
      item_w := substr(fatores_p, i, 1);
       if (item_w = ',') then
           retorno_w := retorno_w + 1;
       end if;
   end loop;
end if;

return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
