CREATE OR REPLACE FUNCTION tof_meta_alta_atingida (nr_atendimento_p text) RETURNS varchar AS $body$
DECLARE


dt_retorno_w		varchar(2) := 'A';
dt_previsto_alta_w		timestamp;

BEGIN

select 	coalesce(max(dt_previsto_alta),clock_timestamp())
into STRICT 	dt_previsto_alta_w
from 	atendimento_paciente
where 	nr_atendimento = nr_atendimento_p;

if (dt_previsto_alta_w < clock_timestamp()) then
	dt_retorno_w := 'N';
end if;

return dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
