CREATE OR REPLACE FUNCTION tp_obter_nr_sequencia ( CD_PESSOA_FISICA_P text, DT_CHAMADO_P timestamp) RETURNS bigint AS $body$
DECLARE


nr_seq_regulacao_w	bigint;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '' AND dt_chamado_p IS NOT NULL AND dt_chamado_p::text <> '') then

select	max(nr_sequencia)
into STRICT	nr_seq_regulacao_w
from 	EME_REGULACAO
where 	1 = 1
and 	cd_pessoa_fisica = cd_pessoa_fisica_p
and	trunc(dt_chamado) = trunc(dt_chamado_p);

end if;

return	nr_seq_regulacao_w;

end;
$body$
LANGUAGE PLPGSQL
