CREATE OR REPLACE FUNCTION trans_obter_se_transport_disp (nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
cd_pessoa_fisica_w	varchar(10);
nr_sequencia_w		bigint;


BEGIN 
 
cd_pessoa_fisica_w := substr(obter_pf_usuario(nm_usuario_p, 'C'),1,10);
begin 
	select	nr_sequencia 
	into STRICT	nr_sequencia_w 
	from	transportador 
	where	cd_pessoa_fisica = coalesce(cd_pessoa_fisica_w, 0) 
	and	ie_situacao = 'A' 
	and	IE_STATUS_RECURSO = 1  LIMIT 1;
exception 
	when 	no_data_found then 
		nr_sequencia_w := 0;
end;
 
If (nr_sequencia_w > 0) then 
	return 'S';
else 
	return 'N';
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
