SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION trans_obter_status_intevalo ( nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


cd_transportador_usuario_w			varchar(10);
ie_status_recurso_w			smallint;
ds_retorno_w				varchar(1);


BEGIN

select	a.cd_pessoa_fisica
into STRICT	cd_transportador_usuario_w
from	usuario a
where	upper(a.nm_usuario_pesquisa) = upper(nm_usuario_p);

select	coalesce(max(ie_status_recurso),1)
into STRICT	ie_status_recurso_w
from	transportador
where	cd_pessoa_fisica  = cd_transportador_usuario_w;

if (ie_status_recurso_w in (1)) then
	ds_retorno_w	:= 'I';	--Inicio
elsif (ie_status_recurso_w = 3) then
	ds_retorno_w	:= 'F'; --Finaliza
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
