CREATE OR REPLACE FUNCTION tre_consiste_dia_semana ( nr_seq_evento_p bigint, dt_presenca_atual_p text) RETURNS varchar AS $body$
DECLARE


vl_retorno_w		varchar(1);
qt_registro_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from  	tre_evento a,
	tre_agenda b,
	tre_agenda_turno c
where    a.nr_seq_agenda = b.nr_sequencia
and	c.nr_seq_agenda_tre = b.nr_sequencia
and	a.nr_sequencia = nr_seq_evento_p
and	c.ie_dia_semana = obter_cod_dia_semana(to_date(dt_presenca_atual_p,'dd/mm/yyyy'))
and	((to_date(dt_presenca_atual_p,'dd/mm/yyyy hh24:mi:ss') between c.dt_inicio_vigencia and fim_dia(c.dt_fim_vigencia)) or ((coalesce(c.dt_inicio_vigencia::text, '') = '') or (coalesce(c.dt_fim_vigencia::text, '') = '')));

if (qt_registro_w > 0) then
	vl_retorno_w := 'S';
else
	vl_retorno_w := 'N';
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
