CREATE OR REPLACE FUNCTION tre_obter_carga_hora_inscrito ( nr_seq_evento_p bigint, cd_pessoa_fisica_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
	1 = Número. ex: 10
	2 = Número por extenso. ex: dez
*/
ds_retorno_w		varchar(200);
retorno_w		varchar(200);


BEGIN

select	sum(carga_horaria)
into STRICT	retorno_w
from (
SELECT	sum(c.qt_carga_horaria) carga_horaria
from	tre_inscrito		a,
	tre_inscrito_presenca	b,
	tre_evento_modulo		c,
	tre_evento		d
where	a.nr_sequencia = b.nr_seq_inscrito
and	d.nr_sequencia = nr_seq_evento_p
and	a.nr_seq_evento = d.nr_sequencia
and	c.nr_seq_evento = d.nr_sequencia
and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
and	a.nr_seq_mod_evento = c.nr_sequencia
group by	nr_seq_modulo,
		c.qt_carga_horaria
) alias2;

if (ie_opcao_p = 1) then
	ds_retorno_w := retorno_w;
elsif (ie_opcao_p = 2) then
	ds_retorno_w := replace(obter_extenso(retorno_w),'Reais .',') horas.');
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
