CREATE OR REPLACE FUNCTION tre_obter_carga_hora_part ( cd_pessoa_fisica_p text, nr_seq_modulo_p bigint, nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE

qt_carga_part_w		double precision;
qt_carga_hora_w		double precision := 0;
qt_carga_hora_aux	double precision;
qt_carga_horaria_w	double precision;
cd_pessoa_fisica_w	varchar(10);
nr_seq_modulo_w		bigint;
nr_sequencia_w		bigint;
nr_seq_evento_w	bigint;
ie_presente_w		varchar(1);
dt_treinamento_w	timestamp;
cd_estabelecimento_w	smallint := wheb_usuario_pck.get_cd_estabelecimento;

C01 CURSOR FOR
	SELECT	nr_seq_evento,
		qt_carga_horaria
	from	tre_evento_modulo
	where	nr_seq_modulo	= nr_seq_modulo_p
	and	nr_sequencia	= nr_sequencia_p
	group by
		nr_seq_evento,
		qt_carga_horaria;

C02 CURSOR FOR
	SELECT  a.dt_dia
	from    dia_v a,
		tre_evento_modulo b
	where	a.dt_dia between b.dt_inicio and b.dt_termino
	and	b.nr_seq_modulo	= nr_seq_modulo_p
	and	b.nr_sequencia	= nr_sequencia_p
	group by a.dt_dia;


BEGIN
open C01;
loop
fetch C01 into
	nr_seq_evento_w,
	qt_carga_horaria_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		dt_treinamento_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
			select	(tre_consistir_hora_treinamento(nr_seq_evento_w, nr_seq_modulo_p,dt_treinamento_w,cd_estabelecimento_w,'CH'))::numeric
			into STRICT	qt_carga_hora_aux
			;

			select	a.ie_presente
			into STRICT	ie_presente_w
			from	tre_inscrito_presenca a,
				tre_inscrito b
			where	a.dt_presenca		= dt_treinamento_w
			and	b.nr_sequencia		= a.nr_seq_inscrito
			and	b.cd_pessoa_fisica	= cd_pessoa_fisica_p;

			if (ie_presente_w = 'S') then
				qt_carga_hora_w := qt_carga_hora_w + qt_carga_hora_aux;
			end if;

		end;
	end loop;
	close C02;

	qt_carga_part_w := coalesce(qt_carga_hora_w,0);

	end;
end loop;
close C01;

return qt_carga_part_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
