CREATE OR REPLACE FUNCTION tre_obter_carga_hor_curso ( nr_evento_p bigint, cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		double precision := 0;
qt_carga_horaria_w	double precision;

c01 CURSOR FOR
	SELECT	 	a.qt_carga_horaria
	FROM	tre_evento_modulo a,
			tre_inscrito b,
			tre_inscrito_presenca c,
			tre_evento		d
	WHERE	c.nr_seq_inscrito = b.nr_sequencia
	AND		b.nr_seq_mod_evento = a.nr_sequencia
	AND		b.nr_seq_evento = d.nr_sequencia
	AND		a.nr_seq_evento = d.nr_sequencia
	AND		c.ie_presente = 'S'
	AND 	(a.qt_carga_horaria IS NOT NULL AND a.qt_carga_horaria::text <> '')
	AND		a.nr_seq_evento = nr_evento_p
	AND		b.cd_pessoa_fisica = cd_pessoa_fisica_p
	GROUP BY	nr_seq_modulo,
			a.qt_carga_horaria;



BEGIN
if (nr_evento_p IS NOT NULL AND nr_evento_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
	open c01;
	loop
	fetch c01 into
		qt_carga_horaria_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		qt_retorno_w := qt_retorno_w + qt_carga_horaria_w;
		end;
	end loop;
	close c01;
	end;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
