CREATE OR REPLACE FUNCTION tre_obter_criterio_phi (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w varchar(200);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

