CREATE OR REPLACE FUNCTION tre_obter_dados_pac_dia (nr_seq_inscrito_p bigint, dt_registro_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
qt_possui_bloqueio_w	bigint;


BEGIN
if (ie_opcao_p = 'C') then
	select	max(nr_sequencia)
	into STRICT	ds_retorno_w
	from	tre_paciente_dia a
	where	a.nr_seq_inscrito = nr_seq_inscrito_p
	and	trunc(a.dt_registro) = trunc(fim_dia(dt_registro_p));
elsif (ie_opcao_p = 'S') then
	select	max(ie_status)
	into STRICT	ds_retorno_w
	from	tre_paciente_dia a
	where	a.nr_seq_inscrito = nr_seq_inscrito_p
	and	trunc(a.dt_registro) = trunc(fim_dia(dt_registro_p));
elsif (ie_opcao_p = 'DC') then
	select	to_char(max(dt_chegada_paciente),'dd/mm/yyyy hh24:mi:ss')
	into STRICT	ds_retorno_w
	from	tre_paciente_dia a
	where	a.nr_seq_inscrito = nr_seq_inscrito_p
	and	trunc(a.dt_registro) = trunc(fim_dia(dt_registro_p));
elsif (ie_opcao_p = 'A') then
	select	max(nr_atendimento)
	into STRICT	ds_retorno_w
	from	tre_paciente_dia a
	where	a.nr_seq_inscrito = nr_seq_inscrito_p
	and	trunc(a.dt_registro) = trunc(fim_dia(dt_registro_p));
elsif (ie_opcao_p = 'SP') then
	select	coalesce(max(ie_status_paciente),'N')
	into STRICT	ds_retorno_w
	from	tre_paciente_dia a
	where	a.nr_seq_inscrito = nr_seq_inscrito_p
	and	trunc(a.dt_registro) = trunc(fim_dia(dt_registro_p));

	if (ds_retorno_w not in ('C','E')) then
		select	count(*)
		into STRICT	qt_possui_bloqueio_w
		from	tre_inscrito_bloqueio
		where	nr_seq_inscrito = nr_seq_inscrito_p
		and	trunc(dt_registro_p) <> trunc(dt_ini_bloqueio)
		and	((trunc(dt_registro_p) <> trunc(dt_fim_bloqueio)) or (coalesce(dt_fim_bloqueio::text, '') = ''))
		and	((dt_registro_p between inicio_dia(dt_ini_bloqueio) and fim_dia(dt_fim_bloqueio)) or (trunc(dt_registro_p) >= trunc(dt_ini_bloqueio) and coalesce(dt_fim_bloqueio::text, '') = ''));

		if (qt_possui_bloqueio_w > 0) then
			ds_retorno_w := 'S';
		end if;
	end if;

elsif (ie_opcao_p = 'CA') then
	select	count(*)
	into STRICT	ds_retorno_w
	from	tre_paciente_dia a
	where	a.nr_seq_inscrito = nr_seq_inscrito_p
	and	trunc(a.dt_registro) = trunc(fim_dia(dt_registro_p))
	and	ie_status_paciente = 'C';

elsif (ie_opcao_p = 'MF') then
	select	max(ds_motivo_status)
	into STRICT	ds_retorno_w
	from	tre_paciente_dia a
	where	a.nr_seq_inscrito = nr_seq_inscrito_p
	and	trunc(a.dt_registro) = trunc(fim_dia(dt_registro_p));
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
