CREATE OR REPLACE FUNCTION tre_obter_dados_treinamento ( nr_seq_curso_p bigint, ie_opcao_p text, ie_cod_desc_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_tipo_w	bigint;
ds_tipo_w	varchar(80);

BEGIN
if (ie_opcao_p = 'TT') then
	select	a.nr_sequencia,
		a.ds_tipo
	into STRICT	nr_seq_tipo_w,
		ds_tipo_w
	from	tre_tipo a,
		tre_curso b
	where	a.nr_sequencia = b.nr_seq_tipo
	and	b.nr_sequencia = nr_seq_curso_p;

	if (ie_cod_desc_p = 'C') then
		return nr_seq_tipo_w;
	else
		return ds_tipo_w;
	end if;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
