CREATE OR REPLACE FUNCTION tre_obter_desc_modulo_aval (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

select	substr(substr(obter_descricao_padrao('TRE_CURSO_MODULO','DS_MODULO',nr_seq_modulo),1,100) || ' - ' ||
	a.ds_evento,1,255)
into STRICT	ds_retorno_w
from	tre_evento a,
	tre_evento_modulo b
where	a.nr_sequencia = b.nr_seq_evento
and	b.nr_sequencia = nr_sequencia_p;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
