SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tre_obter_desc_modulo ( nr_seq_modulo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_opcao_p:
'C' - candidato
'I' - Inscrito
*/
ds_retorno_w		varchar(80);
nr_seq_modulo_w	bigint;


BEGIN

if (ie_opcao_p = 'C') then
	select	nr_seq_modulo
	into STRICT	nr_seq_modulo_w
	from	tre_agenda_modulo
	where	nr_sequencia = nr_seq_modulO_p;
elsif (ie_opcao_p = 'I') then
	select	nr_seq_modulo
	into STRICT	nr_seq_modulo_w
	from	tre_evento_modulo
	where	nr_sequencia = nr_seq_modulo_p;
end if;

select	substr(obter_descricao_padrao('TRE_CURSO_MODULO','DS_MODULO',nr_seq_modulo_w),1,100)
into STRICT	ds_retorno_w
;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
