CREATE OR REPLACE FUNCTION tre_obter_desc_motivo_desist (nr_seq_motivo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(90);


BEGIN

if (nr_seq_motivo_p IS NOT NULL AND nr_seq_motivo_p::text <> '') then

	select	substr(max(ds_motivo_desistencia),1,90)
	into STRICT	ds_retorno_w
	from	tre_motivo_desistencia
	where	nr_sequencia	= nr_seq_motivo_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
