SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tre_obter_modulos_inscrito ( nr_seq_evento_p bigint, cd_pessoa_fisica_p text, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
	1 = primeira coluna, 1-5 modulos
	2 = segunda coluna. 5-10 modulos
	3 = terceira coluna. 10-15 modulos
*/
ds_retorno_w		varchar(4000);
ds_modulo_w		varchar(4000);
i			bigint;

c01 CURSOR FOR
	SELECT	distinct substr(obter_descricao_modulo(c.nr_seq_modulo),1,200) ds_modulo	/*  distinct adicionado por mhbarth, os 381083 */
	from	tre_inscrito		a,
		tre_inscrito_presenca	b,
		tre_evento_modulo	c,
		tre_evento		d
	where	a.nr_sequencia = b.nr_seq_inscrito
	and	d.nr_sequencia = nr_seq_evento_p
	and	a.nr_seq_evento = d.nr_sequencia
	and	c.nr_seq_evento = d.nr_sequencia
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	a.nr_seq_mod_evento = c.nr_sequencia
	AND	b.ie_presente = 'S'
	group by	nr_seq_modulo,
			c.qt_carga_horaria;



BEGIN


open c01;
loop
fetch c01 into
	ds_modulo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (coalesce(i::text, '') = '') then
		i := 0;
	end if;

	i := i + 1;

	if (ie_opcao_p = 1 and i <= 6) then
	ds_retorno_w := ds_retorno_w || ds_modulo_w || chr(13) || chr(13);
	end if;

	if (ie_opcao_p = 2 and i > 6 and i <=11) then
	ds_retorno_w := ds_retorno_w || ds_modulo_w || chr(13) || chr(13);
	end if;

	if (ie_opcao_p = 3 and i > 11 and i <=16) then
	ds_retorno_w := ds_retorno_w || ds_modulo_w || chr(13) || chr(13);
	end if;

	end;
end loop;
close c01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
