CREATE OR REPLACE FUNCTION tre_obter_perc_real_evento ( nr_seq_evento_p bigint) RETURNS bigint AS $body$
DECLARE


pr_retorno_w			double precision;
qt_carga_total_w			double precision;
qt_carga_real_w			double precision;


BEGIN

select	sum(coalesce(qt_carga_horaria,0))
into STRICT	qt_carga_total_w
from	tre_evento_modulo
where	nr_seq_evento = nr_seq_evento_p;

select	sum(coalesce(qt_carga_horaria,0))
into STRICT	qt_carga_real_w
from	tre_evento_modulo
where	nr_seq_evento 		= nr_seq_evento_p
and	ie_status_modulo	= 'F';

if (qt_carga_real_w = 0) then
	pr_retorno_w	:= 0;
elsif (qt_carga_total_w = qt_carga_real_w) then
	pr_retorno_w	:= 100;
else
	pr_retorno_w	:= dividir((qt_carga_real_w * 100),qt_carga_total_w);
end if;
return	pr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
