CREATE OR REPLACE FUNCTION tre_obter_qtde_candidatos (nr_seq_agenda_p bigint, nr_seq_modulo_p bigint) RETURNS bigint AS $body$
DECLARE


qt_candidatos_w		bigint:= 0;


BEGIN

select 	count(*)
into STRICT	qt_candidatos_w
from 	tre_candidato
where 	nr_seq_agenda = nr_seq_agenda_p
and 	((nr_seq_modulo = nr_seq_modulo_p) or (coalesce(nr_seq_modulo::text, '') = '')) /*Candidatos sem módulo, são candidatos para o treinamento todo*/
and	((ie_cancelamento	= 'N') or (coalesce(ie_cancelamento::text, '') = ''));

return qt_candidatos_w;

end;
