SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tre_obter_se_horarios_compat ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

ie_agenda_tre_w bigint;
dt_agenda_w		timestamp;
hr_inicial_w	timestamp;
hr_final_w		timestamp;
ds_retorno_w	varchar(1) := 'S';
cd_pessoa_fisica_w	varchar(10);

C01 CURSOR FOR
	SELECT	trunc(a.dt_agenda) dt_agenda,
			hr_inicial hr_inicial,
			hr_final hr_final
	from (	SELECT	substr(obter_cod_dia_semana(dt_termino - rownum),1,1) ie_dia_semana,
						dt_termino - row_number() OVER () AS dt_agenda
				from (	select	trunc(dt_termino) - trunc(dt_inicio) qt,
										dt_termino
							from		tre_agenda
							where		nr_Sequencia = nr_sequencia_p) a,
						tabela_sistema b ORDER by 2 LIMIT (qt)) a,
			tre_agenda_turno b
	where	((a.ie_dia_semana = b.ie_dia_semana) or (b.ie_dia_semana = 9 and a.ie_dia_semana not in (7,1)))
	and		b.nr_seq_agenda_tre = nr_sequencia_p
	and ((a.dt_agenda between b.dt_inicio_vigencia and fim_dia(b.dt_fim_vigencia)) or ((coalesce(b.dt_inicio_vigencia::text, '') = '') or (coalesce(b.dt_fim_vigencia::text, '') = '')))
	order by 1;

C02 CURSOR FOR
	SELECT	cd_pessoa_fisica
	from	tre_candidato
	where	nr_seq_agenda = nr_sequencia_p;


BEGIN

open C01;
loop
fetch C01 into	dt_agenda_w,
				hr_inicial_w,
				hr_final_w;
EXIT WHEN NOT FOUND or ds_retorno_w = 'N';  /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		cd_pessoa_fisica_w;
	EXIT WHEN NOT FOUND or ds_retorno_w = 'N';  /* apply on C02 */
		begin
		select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ds_retorno_w
		from	agenda_paciente
		where	cd_pessoa_fisica = cd_pessoa_fisica_w
		and		trunc(hr_inicio) = trunc(dt_agenda_w)
		and		to_date('30/12/1899 '|| to_char(hr_inicio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') between hr_inicial_w and hr_final_w
		and		ie_status_agenda not in ('C','B');

		if (ds_retorno_w = 'S') then
			select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
			into STRICT	ds_retorno_w
			from	agenda_consulta
			where	cd_pessoa_fisica = cd_pessoa_fisica_w
			and		trunc(dt_agenda) = trunc(dt_agenda_w)
			and		to_date('30/12/1899 '|| to_char(dt_agenda,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') between hr_inicial_w and hr_final_w
			and		ie_status_agenda not in ('C','B');
		end if;
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
