SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tre_obter_se_pessoa_aprov ( nr_sequencia_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(01) := 'S';
qt_existe_w	bigint;
nr_seq_tipo_w	bigint;


BEGIN
select	nr_seq_tipo
into STRICT	nr_seq_tipo_w
from	tre_curso
where	nr_sequencia 	= nr_sequencia_p;

select	count(*)
into STRICT	qt_existe_w
from	tre_tipo_aprovador
where	nr_seq_tipo	= nr_seq_tipo_w;

if (qt_existe_w > 0) then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	tre_tipo_aprovador
	where	nr_seq_tipo	= nr_seq_tipo_w
	and	cd_pessoa_aprov	= cd_pessoa_fisica_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
