CREATE OR REPLACE FUNCTION tre_obter_se_treinamento_setor ( nr_seq_tipo_p bigint, cd_setor_atendimento_p bigint, ie_padrao_retorno_p text default 'S') RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
qt_existe_w	bigint;


BEGIN
ds_retorno_w := ie_padrao_retorno_p;

select	count(*)
into STRICT	qt_existe_w
from	tre_tipo_setor
where	nr_seq_tipo	= nr_seq_tipo_p;

if (qt_existe_w > 0) then
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	tre_tipo_setor a
	where	a.nr_seq_tipo		= nr_seq_tipo_p
	and	a.cd_setor_atendimento	= cd_setor_atendimento_p;
end if;

return	ds_retorno_w;

end;
