SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tre_retorna_agenda_turma ( nr_seq_curso_p bigint, cd_pessoa_fisica_p text ) RETURNS varchar AS $body$
DECLARE


cd_treinamento_w	varchar(255);
ds_agenda_w		varchar(255);
retorna_w		varchar(1000);


BEGIN

select	max(substr(obter_dados_treinamento(a.nr_seq_curso,'1'),1,200)) cd_treinamento,
	max(a.ds_agenda)
into STRICT	cd_treinamento_w,
	ds_agenda_w
from	tre_agenda a,
	tre_candidato b
where   a.nr_sequencia = nr_seq_curso_p
and	b.nr_seq_agenda = a.nr_sequencia
and	b.cd_pessoa_fisica = cd_pessoa_fisica_p;

retorna_w := cd_treinamento_w || ';' || ds_agenda_w;

return	retorna_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
