CREATE OR REPLACE FUNCTION truncar_data ( DT_PARAMETRO_P timestamp, IE_OPCAO_P text) RETURNS timestamp AS $body$
DECLARE

/* IE_OPCAO_P = */

/* 	D - Dia , M - MÃªs,  A - Ano */

dt_retorno_w		timestamp;


BEGIN
if (substr(upper(ie_opcao_p),1,1)	= 'D') then
	begin
	select trunc(dt_parametro_p,'dd')
	into STRICT	 dt_retorno_w
	;
	end;
elsif (substr(upper(ie_opcao_p),1,1)	= 'M') then
	begin
	select trunc(dt_parametro_p,'month')
	into STRICT	 dt_retorno_w
	;
	end;
elsif (substr(upper(ie_opcao_p),1,1)	= 'A') then
	begin
	select trunc(dt_parametro_p,'year')
	into STRICT	 dt_retorno_w
	;
	end;
end if;

RETURN dt_retorno_w;
END;
$body$
