CREATE OR REPLACE FUNCTION truncar_hora_parametro ( dt_parametro_p timestamp, qt_minutos_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_hora_w	timestamp;


BEGIN

/* Trunca para menor valor */

select	trunc(dt_parametro_p,'hh24') +
	((floor(dividir(campo_numerico(to_char(dt_parametro_p,'mi')),qt_minutos_p)) * qt_minutos_p) / 1440)
into STRICT	dt_hora_w
;

return dt_hora_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
