CREATE OR REPLACE FUNCTION tst_obter_status_legenda ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_legenda_w	bigint;
dt_lib_doc_w	timestamp;


BEGIN
select	count(*)
into STRICT	nr_legenda_w
from	tst_doc_acao_defeito a
where	a.nr_seq_ordem_serv = nr_sequencia_p;

if (nr_legenda_w > 0) then
	begin
	nr_legenda_w := 4260;
	select	coalesce(max(dt_liberacao),null)
	into STRICT	dt_lib_doc_w
	from	tst_doc_acao_defeito a
	where	a.nr_seq_ordem_serv = nr_sequencia_p;

	if (dt_lib_doc_w IS NOT NULL AND dt_lib_doc_w::text <> '') then
		nr_legenda_w := 4261;
	end if;
	end;
else
	nr_legenda_w := 4259;
end if;

return	nr_legenda_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
