SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tst_obter_status_os ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
C - Código		D - Descrição
D		Documentado
L		Liberado
P		Pendente */
qt_registro_w	integer;
qt_liberado_w	integer;
cd_status_w	varchar(1);
ds_status_w	varchar(60);
ds_retorno_w	varchar(60);


BEGIN
select	count(*)
into STRICT	qt_registro_w
from	tst_doc_acao_defeito a
where	a.nr_seq_ordem_serv = nr_sequencia_p;

if (qt_registro_w > 0) then
	begin
	cd_status_w	:= 'D';
	ds_status_w	:= 'Documentado';
	select	count(*)
	into STRICT	qt_liberado_w
	from	tst_doc_acao_defeito a
	where	a.nr_seq_ordem_serv = nr_sequencia_p
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');
	if (qt_liberado_w > 0) then
		begin
		cd_status_w	:= 'L';
		ds_status_w	:= 'Liberado';
		end;
	end if;
	end;
else
	begin
	cd_status_w	:= 'P';
	ds_status_w	:= 'Pendente';
	end;
end if;

if (ie_opcao_p = 'C') then
	ds_retorno_w := cd_status_w;
elsif (ie_opcao_p = 'D') then
	ds_retorno_w := ds_status_w;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION tst_obter_status_os ( nr_sequencia_p bigint, ie_opcao_p text) FROM PUBLIC;

