SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tws_get_exam_revised_status (NR_PRESCRICAO_P bigint, NR_SEQUENCIA_P bigint, vl_param_status_exam_p bigint, nm_user_p text, ie_lab_p text) RETURNS varchar AS $body$
DECLARE




ds_retorno_w		varchar(1) := 'N';



dt_notification_w  timestamp;



dt_notification_read_w timestamp;



count_w bigint;




BEGIN





if (ie_lab_p = 'S') then







select dt_notification into STRICT dt_notification_w from wsuite_notification where nr_primary_key = NR_PRESCRICAO_P and NR_AUXILIARY_KEY = NR_SEQUENCIA_P and ie_notification_type = 'L';







else







select dt_notification into STRICT dt_notification_w from wsuite_notification where nr_primary_key = NR_PRESCRICAO_P and NR_AUXILIARY_KEY = NR_SEQUENCIA_P and ie_notification_type IN ('R','P');







end if;





select count(*)   into STRICT count_w	from wsuite_user_status_exam	where	 NR_PRESCRICAO = nr_prescricao_p and	NR_SEQUENCIA_PRESCR = nr_sequencia_p  order by nr_Sequencia desc LIMIT 1;





if (count_w > 0 and  (dt_notification_w IS NOT NULL AND dt_notification_w::text <> '') ) then



select DT_LEITURA  into STRICT dt_notification_read_w	from wsuite_user_status_exam	where	 NR_PRESCRICAO = nr_prescricao_p and	NR_SEQUENCIA_PRESCR = nr_sequencia_p  order by nr_Sequencia desc LIMIT 1;





	if ( (dt_notification_w -dt_notification_read_w)*1440*60 > 0) then







	ds_retorno_w := 'S';



	else



	ds_retorno_w := 'N';



end if;



end if;



return ds_retorno_w;



END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
