SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tws_get_user_name ( nm_user_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := '';


BEGIN


if (nm_user_p IS NOT NULL AND nm_user_p::text <> '') then

          SELECT A.Nm_Pessoa_Fisica

          INTO STRICT ds_retorno_w

          FROM Wsuite_Solic_Inclusao_Pf A,

            Wsuite_Usuario B

          WHERE A.Nr_Sequencia = B.Nr_Seq_Inclusao_Pf

          AND B.Ds_Login       = nm_user_p;

end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION tws_get_user_name ( nm_user_p text) FROM PUBLIC;

