CREATE OR REPLACE FUNCTION tws_obter_benef_mensalidade ( nr_seq_segurado_p pls_segurado.nr_sequencia%type, nr_seq_mensalidade_p pls_mensalidade.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);


BEGIN
if (ie_opcao_p = 'T') then --Titular
	select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	pls_mensalidade_segurado
	where	nr_seq_mensalidade	= nr_seq_mensalidade_p
	and	nr_seq_segurado		= nr_seq_segurado_p
	and	coalesce(nr_seq_titular::text, '') = '';
elsif (ie_opcao_p = 'P') then --Pagador
	select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	pls_mensalidade		a,
		pls_contrato_pagador	b
	where	b.nr_sequencia		= a.nr_seq_pagador
	and	a.nr_sequencia		= nr_seq_mensalidade_p
	and	b.cd_pessoa_fisica	= (	SELECT	x.cd_pessoa_fisica
						from	pls_segurado x
						where	x.nr_sequencia = nr_seq_segurado_p);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
