CREATE OR REPLACE FUNCTION tws_obter_motivo_cancel_agenda ( cd_motivo_p agenda_motivo_cancelamento.cd_motivo%type, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

  ds_motivo_w varchar(250) := '';

BEGIN
  IF (cd_motivo_p IS NOT NULL AND cd_motivo_p::text <> '')  THEN
    SELECT tws_get_expression_desc_idioma(1011,cd_motivo_p,NULL,cd_estabelecimento_p)
    INTO STRICT ds_motivo_w
;
    IF (coalesce(ds_motivo_w::text, '') = '') THEN
      SELECT DISTINCT a.ds_motivo
      INTO STRICT ds_motivo_w
      FROM agenda_motivo_cancelamento a
      WHERE a.cd_motivo = cd_motivo_p;
    END IF;
  END IF;
RETURN ds_motivo_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
