CREATE OR REPLACE FUNCTION tws_obter_param_tipo_login (ie_tipo_login_w text, cd_funcao_w bigint, nr_seq_funcao_param_w bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

select	max(vl_parametro)
into STRICT	ds_retorno_w
from	wsuite_param_usuario
where	ie_tipo_login = ie_tipo_login_w
and		cd_funcao = cd_funcao_w
and		nr_seq_funcao_param = nr_seq_funcao_param_w;


if (coalesce(ds_retorno_w::text, '') = '') then
	select	max(coalesce(vl_parametro,vl_parametro_padrao))
	into STRICT	ds_retorno_w
	from	funcao_parametro
	where	cd_funcao = cd_funcao_w
	and		nr_sequencia = nr_seq_funcao_param_w;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
