SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tx_obter_classif_receptor ( nr_seq_receptor_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(15);
qt_reg_efetivo_w	integer;
qt_reg_doador_w		integer;


BEGIN

select	count(*)
into STRICT	qt_reg_efetivo_w
from	tx_doador_potencial
where	nr_seq_receptor		= nr_seq_receptor_p
and	ie_doador_efetivo	= 'S';

select	count(*)
into STRICT	qt_reg_doador_w
from	tx_doador_potencial
where	nr_seq_receptor		= nr_seq_receptor_p;


if (qt_reg_efetivo_w > 0) then
	ie_retorno_w	:= 'CDE';
elsif (qt_reg_doador_w > 0) then
	ie_retorno_w	:= 'CDP';
else
	ie_retorno_w	:= 'SDP';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
