CREATE OR REPLACE FUNCTION tx_obter_dados_central (nr_seq_central_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_central_w		varchar(80);
ds_retorno_w		varchar(255);


BEGIN

select	ds_central
into STRICT	ds_central_w
from	tx_central_transpl
where	nr_sequencia	= nr_seq_central_p;

if (ie_opcao_p = 'D') then
	ds_retorno_w	:= ds_central_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
