SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tx_obter_dados_doador ( nr_seq_doador_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
nm_doador_w		varchar(80);
cd_pessoa_fisica_w	varchar(10);
ds_orgao_w		varchar(80);
ds_retorno_w		varchar(255);

/* 
N - Nome 
C - Codigo PF 
*/
 
 

BEGIN 
 
select	substr(obter_nome_pf(cd_pessoa_fisica),1,80), 
	cd_pessoa_fisica 
into STRICT	nm_doador_w, 
	cd_pessoa_fisica_w 
from	tx_doador 
where	nr_sequencia	= nr_seq_doador_p;
 
if (ie_opcao_p = 'N') then 
	ds_retorno_w	:= nm_doador_w;
elsif (ie_opcao_p = 'C') then 
	ds_retorno_w	:= cd_pessoa_fisica_w;
elsif (ie_opcao_p = 'O') then 
	ds_retorno_w	:= ds_orgao_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
