CREATE OR REPLACE FUNCTION tx_obter_dados_laboratorio ( nr_seq_laboratorio_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
cd_cgc_w		varchar(14);
ds_laboratorio_w	varchar(80);
ds_retorno_w		varchar(255);
	

BEGIN 
 
select	cd_cgc, 
	substr(obter_dados_pf_pj('',cd_cgc, 'N'),1,80) 
into STRICT	cd_cgc_w, 
	ds_laboratorio_w 
from	tx_laboratorio 
where	nr_sequencia	= nr_seq_laboratorio_p;
 
if (ie_opcao_p = 'C') then 
	ds_retorno_w	:= cd_cgc_w;
elsif (ie_opcao_p = 'R') then 
	ds_retorno_w	:= ds_laboratorio_w;
end if;
 
return	ds_retorno_w;
	 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
