SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tx_obter_dados_locus ( nr_seq_locus_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/*
1 - classe
2 - descrição locus
*/
ie_classe_locus_w varchar(2);
ds_locus_hla_w  varchar(20);
ds_classe_w	varchar(20);
ds_retorno_w  varchar(20);

BEGIN

select  max(ie_classe_locus),
	max(ds_locus_hla),
	max(CASE WHEN ie_classe_locus='C1' THEN wheb_mensagem_pck.get_texto(803125)  ELSE wheb_mensagem_pck.get_texto(803126) END )
into STRICT 	ie_classe_locus_w,
	ds_locus_hla_w,
	ds_classe_w
from 	tx_locus_hla
where 	nr_sequencia = nr_seq_locus_p;

if (ie_opcao_p = 1) then
	ds_retorno_w := ie_classe_locus_w;
elsif (ie_opcao_p = 2) then
	ds_retorno_w := ds_locus_hla_w;
elsif (ie_opcao_p = 3) then
	ds_retorno_w := ds_classe_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
