SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tx_obter_dados_receptor ( nr_seq_receptor_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
D - nm_receptor 
C - Cd_pessos_fisica 
TO - Tipo do órgão 
*/
 
 
ds_retorno_w		varchar(255);
nm_receptor_w		varchar(255);
cd_pessoa_fisica_w	varchar(10);
ie_tipo_orgao_w		varchar(15);
nm_orgao_w		varchar(80);


BEGIN 
 
select	substr(max(obter_nome_pf(cd_pessoa_fisica)),1,255), 
	max(cd_pessoa_fisica), 
	substr(max(tx_obter_dados_orgao(nr_seq_orgao, 'T')),1,15), 
	substr(max(tx_obter_dados_orgao(nr_seq_orgao, 'O')),1,80) 
into STRICT	nm_receptor_w, 
	cd_pessoa_fisica_w, 
	ie_tipo_orgao_w, 
	nm_orgao_w 
from	tx_receptor 
where 	nr_sequencia = nr_seq_receptor_p;
 
if (ie_opcao_p = 'D') then 
	ds_retorno_w 	:= nm_receptor_w;
elsif (ie_opcao_p = 'C') then 
	ds_retorno_w	:= cd_pessoa_fisica_w;
elsif (ie_opcao_p = 'TO') then 
	ds_retorno_w	:= ie_tipo_orgao_w;
elsif (ie_opcao_p = 'O') then 
	ds_retorno_w	:= nm_orgao_w;
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
