CREATE OR REPLACE FUNCTION tx_obter_dados_relacao_rec_doa (nr_seq_rec_doa_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_grau_parentesco_w	varchar(90);
ds_relacao_w		varchar(90);
ds_resultado_w		varchar(90);


BEGIN

if (nr_seq_rec_doa_p IS NOT NULL AND nr_seq_rec_doa_p::text <> '') then

	select 	max(DS_RELACAO),
		max(tx_obter_desc_grau_parentesco(NR_SEQ_GRAU_PARENTESCO))
	into STRICT	ds_relacao_w,
		ds_grau_parentesco_w
	from 	tx_relacao_recep_doador
	where	nr_sequencia = nr_seq_rec_doa_p;

	if (ie_opcao_p = 0) then

		ds_resultado_w	:= ds_relacao_w;

	elsif (ie_opcao_p = 1) then

		ds_resultado_w	:= ds_grau_parentesco_w;

	end if;

end if;

return ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
