CREATE OR REPLACE FUNCTION tx_obter_descricao_droga ( nr_seq_droga_p bigint) RETURNS varchar AS $body$
DECLARE

ds_droga_w	varchar(255);

BEGIN

if (nr_seq_droga_p > 0) then

	select 	ds_droga
	into STRICT	ds_droga_w
	from	tx_drogas_paciente
	where 	nr_sequencia = nr_seq_droga_p;

end if;

return ds_droga_w;

end;
