SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tx_obter_metodo_lab ( cd_estabelecimento_p bigint, nr_seq_laboratorio_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_metodo_w	bigint 	:= 0;
nr_seq_exame_w	bigint;


BEGIN

if (ie_opcao_p = 'HLA') then

	select 	max(nr_seq_exame_hla)
	into STRICT	nr_seq_exame_w
	from	tx_parametros
	where	cd_estabelecimento = cd_estabelecimento_p;


elsif (ie_opcao_p = 'PAINEL') then

	select 	max(nr_seq_exame_painel)
	into STRICT	nr_seq_exame_w
	from	tx_parametros
	where	cd_estabelecimento = cd_estabelecimento_p;


elsif (ie_opcao_p = 'CROSS') then

	select 	max(nr_seq_exame_crossmatch)
	into STRICT	nr_seq_exame_w
	from	tx_parametros
	where	cd_estabelecimento = cd_estabelecimento_p;

elsif (ie_opcao_p = 'MELD') then

	select 	max(nr_seq_exame_meld)
	into STRICT	nr_seq_exame_w
	from	tx_parametros
	where	cd_estabelecimento = cd_estabelecimento_p;

end if;

if (nr_seq_exame_w IS NOT NULL AND nr_seq_exame_w::text <> '') then

	select 	max(nr_seq_metodo)
	into STRICT	nr_seq_metodo_w
	from  	tx_lab_metodo
	where	nr_seq_laboratorio = nr_seq_laboratorio_p
	and	nr_seq_exame = nr_seq_exame_w;


end if;

return nr_seq_metodo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
