SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tx_obter_orgaos_equipe (nr_seq_equipe_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_orgao_w		varchar(80);

c01 CURSOR FOR
	SELECT	substr(obter_valor_dominio(2185,ie_orgao),1,80)
	from	tx_equipe_orgao
	where	nr_seq_equipe	= nr_seq_equipe_p
	order by 1;


BEGIN

open c01;
loop
fetch c01 into
	ds_orgao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w	:= ds_orgao_w;
	else
		ds_retorno_w	:= ds_retorno_w || '/' || ds_orgao_w;
	end if;
	end;
end loop;
close c01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
