CREATE OR REPLACE FUNCTION tx_obter_qt_tratamento ( nr_seq_receptor_p bigint, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_tratamento_w		bigint;
qt_1_w			integer;
qt_2_w			integer;
qt_total_w		integer;

/*
Opção: Tipo de tratamento

CO   = Conservador
CAPD = Dialise Peritoneal Ambulatorial Continua
DPA  = Dialise Peritoneal Automatizada
CPI  = Dialise Peritoneal Intermitente
HD   = Hemodiálise
PT   = Pós-Transplante
TX   = Pré-Transplante
TR   = Transplante

*/
BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_seq_receptor_p IS NOT NULL AND nr_seq_receptor_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then

select 	count(*)
into STRICT	qt_1_w
from	paciente_tratamento d,
	tx_receptor c,
	tx_registro_snt b,
	tx_central_transpl a
where	a.nr_sequencia		= b.nr_seq_central
and	b.nr_seq_receptor	= c.nr_sequencia 
and	c.cd_pessoa_fisica	= d.cd_pessoa_fisica
and	c.nr_sequencia		= d.nr_seq_receptor
and	d.ie_tratamento 	= ie_opcao_p
and	ie_opcao_p not in ('HD','CH','CHD','CHDF','CHDI','PAF','SCUF','SLED','DDC','IHF')
and	d.cd_pessoa_fisica	= cd_pessoa_fisica_p
and	d.nr_seq_receptor	= nr_seq_receptor_p
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	coalesce(d.dt_final_tratamento::text, '') = ''
and	substr(tx_obter_se_paciente_ativo(d.nr_seq_receptor),1,1) = 'S';

select  count(*)
into STRICT	qt_2_w
from	paciente_tratamento d,
	tx_receptor c,
	tx_registro_snt b,
	tx_central_transpl a
where	a.nr_sequencia		= b.nr_seq_central
and	b.nr_seq_receptor	= c.nr_sequencia 
and	c.cd_pessoa_fisica	= d.cd_pessoa_fisica
and	c.nr_sequencia		= d.nr_seq_receptor
and	d.ie_tratamento 	= ie_opcao_p
and	ie_opcao_p in ('HD','CH','CHD','CHDF','CHDI','PAF','SCUF','SLED')
and	d.cd_pessoa_fisica	= cd_pessoa_fisica_p
and	d.nr_seq_receptor	= nr_seq_receptor_p
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	coalesce(d.dt_final_tratamento::text, '') = ''
and	substr(tx_obter_se_paciente_ativo(d.nr_seq_receptor),1,1) = 'S';

qt_total_w := qt_1_w+qt_2_w;

end if;

qt_tratamento_w := qt_total_w;

return qt_tratamento_w;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
